const LIVE_GIF = `data:image/gif;base64,`
export function IconForLive({
  active = false,
  ...props
}: {
  active?: boolean
  width?: string | number
  height?: string | number
  className?: string
}) {
  if (active) {
    return <img {...props} src={LIVE_GIF} />
  } else {
    return <IconMaterialSymbolsBarChart {...props} />
  }
}

